INSERT INTO `main_content` VALUES (334, 5, 30, 0, 'Third Grade', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2004-02-19', '2014-05-31', '2014-05-31', '2', '18', '', '2004-02-19', '2004-08-19', 1, 100, '<P></P><FONT size=2>\r\n<P><FONT face="arial, helvetica, sans-serif" size=3>Welcome to third grade at PCS! Third grade at Peoria Christian School is an exciting year of learning and fun! Our Christ-centered curriculum leads students into a world of change as they begin to develop more responsibility as well as independent study skills. Our year in third grade includes daily devotions and prayer, special programs and service projects, and two annual field trips to Wildlife Prairie Park and the Peoria Civic Center for a production of Charles Dickens "A Christmas Carol." In third grade we focus on multiplication mastery. We enjoy creative writing, book reports and a science notebook project on vertebrates. Third grade at Peoria Christian promises to be a year of growth, not only as students, but also as godly young men and women.</FONT></P>\r\n<P></P></FONT>', 73, 2, 0);
