INSERT INTO `main_content` VALUES (325, 1, 80, 0, 'Contact PCS', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2004-02-16', '2014-05-31', '2014-05-31', '2', '2', '', '2004-02-16', '2004-02-16', 1, 100, '<P>\r\n<TABLE cellSpacing=0 cellPadding=10 width="100%" border=1>\r\n<TBODY>\r\n<TR vAlign=top>\r\n<TD width="50%">\r\n<P>Main Campus:</P>\r\n<BLOCKQUOTE>\r\n<P>&nbsp;(Elementary School, High School, Business Office)</P>\r\n<P>Peoria Christian School<BR>3506 N California Av<BR>Peoira, IL 61603</P>\r\n<P>309.686.4500 (phone)<BR>309.686.4729 (fax)</P>\r\n<P><A href="mailto:pcsmail@peoriachristian.org">pcsmail@peoriachristian.org</A></P>\r\n<P>&nbsp;</P></BLOCKQUOTE></TD>\r\n<TD width="50%">\r\n<P>Monroe Campus:</P>\r\n<BLOCKQUOTE>\r\n<P>&nbsp;(Middle School)</P>\r\n<P>Peoria Christian Middle School<BR>3725 N Monroe Av<BR>Peoria Heights, IL 61616</P>\r\n<P>309.681.0500 (phone)<BR>309.681.9371 (fax)</P>\r\n<P><A href="mailto:pcms@peoriachristian.org">pcms@peoriachristian.org</A></P>\r\n<P>&nbsp;</P></BLOCKQUOTE></TD></TR><!--\r\n<TR>\r\n<TD colSpan=2>\r\n<P align=center>&nbsp;</P>\r\n</blockquote>\r\n</TD></TR>\r\n--></TBODY></TABLE></P>', 0, -1, 0);
