INSERT INTO `main_content` VALUES (328, 5, 0, 0, 'Elementary', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2003-10-15', '2005-05-31', '2005-05-31', '99999', '18', '', '2003-10-23', '2004-08-13', 0, 100, '<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><FONT face=Arial size=3>Thank you for your interest in <?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /><st1:place><st1:PlaceName>Peoria</st1:PlaceName> <st1:PlaceName>Christian</st1:PlaceName> <st1:PlaceName>School</st1:PlaceName></st1:place>.<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>Our elementary, including </FONT><FONT face=Arial size=3>three-year-old Pre-Kindergarten through fourth grades, focus on providing a solid foundation academically in reading and math.<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>We build on a phonetic approach to reading in Kindergarten and expand those skills during the elementary years.<SPAN style="mso-spacerun: yes">&nbsp; <IMG alt="" hspace=8 src="http://www.peoriachristian.org/resources/es/bgardnerWelcome.jpg" align=right vspace=8 border=1></SPAN>We have a strong math curriculum, which includes proficiency in math facts and using hands-on activities for learning concepts.<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>Character development and spiritual growth are important goals of our elementary program.<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>We use a Bible curriculum, including Scripture memory, but we also integrate Biblical truths throughout the day.</FONT></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p><FONT face=Arial size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><FONT face=Arial size=3>Our teachers maintain a personal relationship with Jesus Christ. <SPAN style="mso-spacerun: yes">&nbsp;</SPAN>We guide and direct the children out of a prayerful heart of love and concern.<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>Our certified teachers plan a variety of activities considering the learning style differences of the students.</FONT></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p><FONT face=Arial size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><FONT size=3><FONT face=Arial>We would be happy to answer any questions or give a tour of our facilities if you are interested.<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>Please call me if I can provide further help.<SPAN style="mso-spacerun: yes">&nbsp;&nbsp; </SPAN></FONT></FONT></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><FONT size=3><FONT face=Arial><SPAN style="mso-spacerun: yes"></SPAN></FONT></FONT>&nbsp;</P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><FONT size=3><FONT face=Arial><SPAN style="mso-spacerun: yes">Sincerely,</SPAN></FONT></FONT></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><FONT size=3><FONT face=Arial><SPAN style="mso-spacerun: yes"></SPAN></FONT></FONT>&nbsp;</P><FONT size=3><FONT face=Arial><SPAN style="mso-spacerun: yes">\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><FONT face=Arial size=3><EM><STRONG>Becky Gardner</STRONG></EM></FONT></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p><FONT face=Arial size=3><STRONG>Elementary School Principal</STRONG></FONT></o:p></P></SPAN></FONT></FONT>', 67, -1, 0);
INSERT INTO `main_content` VALUES (323, 3, 10, 0, 'Homework Hotline', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2003-10-15', '2005-05-31', '2005-05-31', '99999', '18', '', '2003-10-23', '2004-08-13', 0, 100, '<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><?xml:namespace prefix = v /><v:shape id=_x0000_s1026 style="MARGIN-TOP: 24pt; Z-INDEX: 1; MARGIN-LEFT: 455.75pt; WIDTH: 51.4pt; POSITION: absolute; HEIGHT: 124.9pt; mso-wrap-edited: f" wrapcoords="19313 52 18551 105 18678 418 20584 889 12579 1412 9911 1621 8386 2510 127 3243 -127 3609 0 4236 508 5073 2414 6747 5464 8420 4447 10094 4193 10931 4320 11924 4701 12604 5464 13441 5591 15115 6099 16788 6353 18462 2414 19299 381 19456 127 19874 889 20136 4701 20972 5718 21548 5845 21548 7115 21548 7496 21548 14612 20972 14485 20606 13976 20136 14104 19299 13722 18462 11308 15952 11689 13441 16391 13441 16645 13127 14231 12604 13214 11768 15882 11768 19440 11297 19313 10931 12833 7584 10927 6747 13849 5910 15501 5073 16264 4236 16391 3400 15501 2563 18169 2144 17788 1726 21600 1360 21600 366 21346 105 20711 52 19313 52" type="#_x0000_t75"><v:imagedata src="file:///C:\\DOCUME~1\\JORDAN~1.PCS\\LOCALS~1\\Temp\\msohtml1\\08\\clip_image001.wmz" o:title="AMDOUBT"></v:imagedata><?xml:namespace prefix = w /><w:wrap type="tight"></w:wrap></v:shape><FONT size=3><SPAN style="FONT-FAMILY: Arial"></SPAN></FONT></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt" align=center><FONT size=3><SPAN style="FONT-FAMILY: Arial"></SPAN></FONT></P><FONT size=3><SPAN style="FONT-FAMILY: Arial"></SPAN></FONT>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt" align=center><IMG alt="Homework Hotline" hspace=0 src="http://www.peoriachristian.org/resources/mbosma/HomeworkHotline.gif" align=top border=0></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt">&nbsp;</P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><FONT size=3><SPAN style="FONT-FAMILY: Arial">Homework Hotline is now available on-line!<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>Daily assignments will be posted by </SPAN><?xml:namespace prefix = st1 /><st1:time Minute="0" Hour="13"><SPAN style="FONT-FAMILY: Arial">1:00 p.m.</SPAN></st1:time><SPAN style="FONT-FAMILY: Arial"> every school day.<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>In addition to the website, homework will also be available by calling the </SPAN><st1:State><st1:place><SPAN style="FONT-FAMILY: Arial">California</SPAN></st1:place></st1:State><SPAN style="FONT-FAMILY: Arial"> campus.<SPAN style="mso-spacerun: yes">&nbsp; </SPAN><?xml:namespace prefix = o /><o:p></o:p></SPAN></FONT></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN style="FONT-FAMILY: Arial"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN style="FONT-FAMILY: Arial"><FONT size=3>The on-line Homework Hotline allows us to provide more information and details than we can do over the phone.<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>Not only will we give you the daily homework assignments, we will also include the work that was done in the classroom, upcoming quizzes and tests, and future assignments.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN style="FONT-FAMILY: Arial"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN style="FONT-FAMILY: Arial"><FONT size=3>So whether you were out-of-school or simply forgot what your assignments were for today, you can find out by checking out the Homework Hotline page for your grade.</FONT></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN style="FONT-FAMILY: Arial"></SPAN>&nbsp;</P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN style="FONT-FAMILY: Arial"><FONT size=3>You will need Adobe Acrobat / Adobe Reader.&nbsp; You can download it freely at:</FONT></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt" align=center><SPAN style="FONT-FAMILY: Arial"><FONT size=3>&nbsp;<A href="http://www.adobe.com/products/acrobat/readstep2.html" target=_blank><IMG alt="" hspace=0 src="http://www.peoriachristian.org/resources/ms/get_adobe_reader.gif" align=baseline border=0></A></FONT></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN style="FONT-FAMILY: Arial"><FONT size=3></FONT></SPAN>&nbsp;</P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt" align=center><SPAN style="FONT-FAMILY: Arial"><FONT size=3><A href="http://www.peoriachristian.org/resources/mbosma/HomeworkHotlineGrade5.pdf" target=_blank>Homework Hotline for 5th Grade</A></FONT></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt" align=center><SPAN style="FONT-FAMILY: Arial"><FONT size=3><A href="http://www.peoriachristian.org/resources/mbosma/HomeworkHotlineGrade6.pdf" target=_blank>Homework Hotline for 6th Grade</A></FONT></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt" align=center><SPAN style="FONT-FAMILY: Arial"><FONT size=3><A href="http://www.peoriachristian.org/resources/mbosma/HomeworkHotlineGrade7.pdf" target=_blank>Homework Hotline for 7th Grade</A></FONT></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt" align=center><SPAN style="FONT-FAMILY: Arial"><FONT size=3><A href="http://www.peoriachristian.org/resources/mbosma/HomeworkHotlineGrade8.pdf" target=_blank>Homework Hotline for 8th Grade</A></FONT></SPAN></P>', 5, 2, 37);
