INSERT INTO `main_content` VALUES (300, 5, 60, 0, 'LaHood Visits Elementary School', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2004-02-14', '2004-05-31', '2004-05-31', '2', '2', '', '2004-02-14', '2004-02-14', 1, 100, '<P>\r\n<P><IMG style="WIDTH: 28px; HEIGHT: 30px" alt="" hspace=8 src="http://www.pcsfamily.org/htmlarea/resources/es/LaHood2.jpg" align=left vspace=8 border=1>On Monday morning, February 2, Congressman Ray LaHood visited PCS. At an Elementary School assembly, he presented to PCS Administrator Steve Hutton an American flag that had flown over the US Capitol.&nbsp; Following the presentation, he spoke to the Elementary students and answered several questions from students.&nbsp; </P>\r\n<P><IMG alt="" hspace=8 src="../resources/es/LaHood1.jpg" align=left vspace=8 border=1>Ray LaHood represents the people of the 18th Congressional District of Illinois. He is serving his fifth term in Congress.&nbsp; </P>', 0, -3, 0);
INSERT INTO `main_content` VALUES (301, 5, 60, 0, 'LaHood Visits Elementary School', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2004-02-14', '2004-05-31', '2004-05-31', '2', '2', '', '2004-02-14', '2004-02-14', 1, 100, '\r\n<P><IMG style="WIDTH: 28px; HEIGHT: 30px" alt="" hspace=8 \r\nsrc="../resources/es/LaHood2.jpg" align=left \r\nvspace=8 border=1>On Monday morning, February 2, Congressman Ray LaHood visited \r\nPCS. At an Elementary School assembly, he presented to PCS Administrator Steve \r\nHutton an American flag that had flown over the US Capitol.&nbsp; Following the \r\npresentation, he spoke to the Elementary students and answered several questions \r\nfrom students.&nbsp; </P>\r\n<P><IMG alt="" hspace=8 src="../resources/es/LaHood1.jpg" align=left vspace=8 \r\nborder=1>Ray LaHood represents the people of the 18th Congressional District of \r\nIllinois. He is serving his fifth term in Congress.&nbsp; </P>', 46, -3, 0);
INSERT INTO `main_content` VALUES (302, 100, 0, 0, 'ITS Main Page', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2004-02-12', '2010-05-31', '2004-05-31', '2', '100000', '', '2004-02-12', '2004-02-16', 10, 100, '<TABLE cellSpacing=0 cellPadding=10 width="100%" border=0>\r\n<TBODY>\r\n<TR>\r\n<TD vAlign=top>\r\n<P><IMG height=218 src="../resources/jm/jmacleanWelcome.jpg" width=250 border=1></P></TD>\r\n<TD>\r\n<P>Thank you for visiting the IT Services area of the PCS website! This area contains information and resources for PCS students, teachers, staff and administrators - as well as for visitors. </P>\r\n<P>To find out more about IT Services, look under General Information to find our Philosophy and Mission Statement. If you are wondering, you can also check out What is IT anyway?</P>\r\n<P>I hope you find what you are looking for. If you don\'t, or if you have any questions or comments, please email (<A href="mailto:jmaclean@peoriachristian.org">jmaclean@peoriachristian.org</A>) or call (309.360.3219) me.</P>\r\n<P>-John MacLean<BR>IT Services Director</P></TD></TR></TBODY></TABLE>', 47, -3, 0);
INSERT INTO `main_content` VALUES (303, 100, 10, 0, 'Philosophy', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2004-02-16', '2014-05-31', '2014-05-31', '2', '2', '', '2004-02-16', '2004-02-16', 200, 100, '<P align=center><FONT face="Arial, Helvetica, sans-serif" size=6>IT Services - Philosophy</FONT></P>\r\n<P>At PCS, we use information technology (IT) to enhance education. IT resources are tools and, like other educational tools, we aim to use them appropriately  to help us teach, learn and administer more effectively and more efficiently. We also seek to be thoughtful and careful about the impact of technology on the PCS community.</P>', 0, 2, 0);
INSERT INTO `main_content` VALUES (304, 100, 10, 0, 'Mission Statement', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2004-02-16', '2014-05-31', '2014-05-31', '2', '2', '', '2004-02-16', '2004-02-16', 200, 100, '<P align=center><FONT face="Arial, Helvetica, sans-serif" size=6>IT Services - Mission</FONT></P>\r\n<P align=left>The PCS IT Services Department exists to:<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; make IT resources and services accessible to PCS students, teachers, staff and administrators.<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; support the appropriate use of these resources and services for activities that are consistent with PCSs mission and goals.</P>\r\n<P align=left>IT Services work involves: resource administration; training; help desk services; and development of information management solutions. </P>', 0, -3, 0);
INSERT INTO `main_content` VALUES (305, 100, 10, 0, 'Mission Statement', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2004-02-16', '2014-05-31', '2004-05-31', '2', '2', '', '2004-02-16', '2004-02-16', 200, 100, '<P align=center><FONT face="Arial, Helvetica, sans-serif" size=6>IT Services - Mission Statement</FONT></P>\r\n<P align=left>The PCS IT Services Department exists to:</P>\r\n<BLOCKQUOTE>\r\n<P align=left> make IT resources and services accessible to PCS students, teachers, staff and administrators.</P>\r\n<P align=left> support the appropriate use of these resources and services for activities that are consistent with PCSs mission and goals.</P></BLOCKQUOTE>\r\n<P align=left>IT Services work involves: resource administration; training; help desk services; and development of information management solutions. </P>', 50, 2, 0);
INSERT INTO `main_content` VALUES (306, 100, 10, 0, 'Summary of IT at PCS', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2004-02-16', '2014-05-31', '2014-05-31', '2', '2', '', '2004-02-16', '2004-02-16', 300, 100, '<P align=center><FONT face="Arial, Helvetica, sans-serif" size=6>Summary of IT at PCS</FONT></P>\r\n<P>At Peoria Christian School, we desire to use information technology (IT) to enhance education. IT resources are tools and, like other educational tools, our aim is to use them appropriately  to help us teach and learn more effectively and more efficiently. </P>\r\n<P>In order to maximize the accessibility of our IT tools we use wireless networking technology. Teachers and students can easily access the Internet, the school intranet, printers, and email from anywhere on campus. As a result, they are more likely to use these tools. </P>\r\n<P>All teachers are issued a wireless notebook computer that they can use for lesson preparation, research, and administrative tasks. Teachers can also check out a data projector to make presentations to their classes. </P>\r\n<P>Teachers can also reserve a computer cart (containing 30 wireless notebook computers). When using a cart, the teacher can provide overall direction and guidance for the class as well as providing each student an opportunity to focus on his/her specific needs or interests. Some examples of computer cart-based activities include: research; development of student presentations; reviewing materials on textbook companion CD-ROMs; recording and analyzing data in a science lab; email exchanges in foreign language classes. In order to complete out of class assignments, students also can access computers, printers, scanners, network resources, as well as computer-based materials in the Library Media Center and in open labs. </P>\r\n<P>The IT Services Department and the Library Media Center work together to ensure that our IT resources are accessible to our students, teachers, and administrative staff. IT Services work involves IT training and support as well as hardware, software and network resource administration. The Library Media Center staff works with faculty to acquire quality computer-based information materials that will support our curriculum. </P>', 0, 2, 0);
INSERT INTO `main_content` VALUES (307, 100, 10, 0, 'What is IT anyway?', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2004-02-16', '2014-05-31', '2014-05-31', '2', '2', '', '2004-02-16', '2004-02-16', 400, 100, '<P align=center><FONT face="Arial, Helvetica, sans-serif" size=6>What is IT anyway?</FONT></P>\r\n<P>IT stands for Information Technology. The TECHNOLOGY part is pretty obvious - computers, printers, software, networks, etc. But the reason we call it INFORMATION technology is because it is really all about information - or it should be. We have these technology tools so that we can manage information more efficiently and effectively - so that we can make information useful to us. This is called the "Information Age" because we now have the ability to store, process and transmit massive amounts of information - as we have never had before. </P>\r\n<P>At a school IT can also stand for Instructional Technology. Again the emphasis is what is being done with the technology. </P>\r\n<P>Our goal at PCS is to use these information and instructional tools appropriately to help us teach, learn and administer more effectively and efficiently.</P>', 0, 2, 0);
INSERT INTO `main_content` VALUES (308, 100, 0, 0, 'ITS Main Page', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2004-02-12', '2010-05-31', '2004-05-31', '2', '100000', '', '2004-02-12', '2004-03-01', 0, 100, '<TABLE cellSpacing=0 cellPadding=10 width="100%" border=0>\r\n<TBODY>\r\n<TR>\r\n<TD vAlign=top>\r\n<P><IMG height=218 src="../resources/jm/jmacleanWelcome.jpg" width=250 border=1></P></TD>\r\n<TD>\r\n<P>Thank you for visiting the IT Services area of the PCS website! This area contains information and resources for PCS students, teachers, staff and administrators - as well as for visitors. </P>\r\n<P>To find out more about IT Services, look under <A href="index.php?Department=10">General Information</A> to find our Philosophy and Mission Statement. If you are wondering, you can also check out What is IT anyway?</P>\r\n<P>I hope you find what you are looking for. If you don\'t, or if you have any questions or comments, please email (<A href="mailto:jmaclean@peoriachristian.org">jmaclean@peoriachristian.org</A>) or call (309.360.3219) me.</P>\r\n<P>-John MacLean<BR>IT Services Director</P></TD></TR></TBODY></TABLE>', 53, 2, 0);
INSERT INTO `main_content` VALUES (309, 10, 0, 0, 'Advancement Main Page', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2004-02-12', '2050-06-30', '2004-05-31', '6', '6', '', '2004-02-12', '2004-02-16', 1, 100, '<P>Welcome to the Advancement Departments Home Page.&nbsp; We are thankful that you have taken the time to visit and hope you enjoy the accompanying pages.&nbsp; If you wish to contact me please call 309-686-4500 Ex: 33 or email <A href="mailto:jkammerer@peoriachristian.com">jkammerer@peoriachristian.com</A>.&nbsp; Thank you again for visiting our site.</P>\r\n<P>Joe Kammerer</P>\r\n<P>Director of Advancement</P>', 54, -1, 0);
