INSERT INTO `main_content` VALUES (400, 11, 25, 0, 'IT Services Requets System', 0, '0000-00-00', 1, 0, '0000-00-00', 1, 0, '0000-00-00', 1, '2004-08-13', '2010-06-01', '2010-06-01', '14', '14', '', '2004-08-13', '2004-08-13', 1, 100, '<META http-equiv=Content-Language content=en-us>\r\n<META content="Microsoft FrontPage 5.0" name=GENERATOR>\r\n<META content=FrontPage.Editor.Document name=ProgId>\r\n<DIV align=center>\r\n<CENTER>\r\n<P>&nbsp;</P>\r\n<TABLE style="BORDER-COLLAPSE: collapse" borderColor=#111111 height=141 cellSpacing=0 cellPadding=0 width=614 border=0>\r\n<TBODY>\r\n<TR>\r\n<TD width=610 height=42>\r\n<DIV align=center><STRONG><FONT face="Arial, Helvetica, sans-serif" size=5>IT Services Request System</FONT></STRONG></DIV></TD></TR>\r\n<TR>\r\n<TD height=19>\r\n<P align=left><FONT face="Arial, Helvetica, sans-serif" size=2>Here you can post or view existing service requests for the IT Department</FONT></P>\r\n<P align=left><STRONG><A href="../cgi-bin/itservices/requestsystem.cgi?action=post"><FONT face="Arial, Helvetica, sans-serif">Post a Service Request</FONT></A></STRONG></P>\r\n<P align=left><FONT face="Arial, Helvetica, sans-serif" size=2>Go here if you have a problem regarding your computer or the PCS network to submit a service request into the management system. This is the preferred &amp; fastest method for informing the IT Department of your needs</FONT></P>\r\n<P align=left><FONT face="Arial, Helvetica, sans-serif" size=3><STRONG><U><A href="../cgi-bin/itservices/requestsystem.cgi?action=view">View Pending Service Requests</A></U></STRONG></FONT></P>\r\n<P align=left><FONT face="Arial, Helvetica, sans-serif" size=2>Go here to see if your problem is already in the work queue for answering, or if you would like to see the status on a previously submitted service requests</FONT></P>\r\n<P align=left><STRONG><A href="../cgi-bin/itservices/requestsystem.cgi?action=Kbase"><FONT face="Arial, Helvetica, sans-serif">View Solved Requests</FONT></A></STRONG></P>\r\n<P align=left><FONT face="Arial, Helvetica, sans-serif" size=2>Go here to see if your problem has already been solved by the IT Services department and been added to the knowledgebase with instructions on how to fix it</FONT></P>\r\n<DIV align=center>\r\n<P align=left><STRONG><FONT face="Arial, Helvetica, sans-serif"><A href="../cgi-bin/itservices/requestsystem.cgi?action=Edit">Admin View</A></FONT></STRONG></P>\r\n<P align=left><FONT face="Arial, Helvetica, sans-serif" size=2>IT Services staff only</FONT></P>\r\n<P align=left><STRONG><FONT face="Arial, Helvetica, sans-serif">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <A href="../../services/index.php">Back to Services Home</A>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </FONT><FONT face="Arial, Helvetica, sans-serif"><A href="http://peoriachristian.org">Back to PCS Home</A></FONT></STRONG></P>\r\n<P align=left>&nbsp;</P>\r\n<P align=left>&nbsp;</P>\r\n<P>&nbsp;</P></DIV></TD></TR>\r\n<TR>\r\n<TD height=19>&nbsp;</TD></TR></TBODY></TABLE></CENTER></DIV>', 0, 2, 0);
